            .current-exhibition{
                padding: 0 10%;
                .current-exhibition-image{
                    width: 100%;
                    height: 780px;
                    display: block;
                    margin: 0 auto;
                    background-color: #606060;
                }
                .current-exhibition-list{
                    display: flex;
                    margin: auto;
